mapper.select:

select m.poster_path, m.title, r.rating, m.movie_id
from movie_dup m
join rating r
on m.movie_id = r.movie_id
where r.user_id = #{user_id}
order by m.title;


select m.poster_path, m.title, r.rating, m.movie_id
from movie_dup m
join rating r
on m.movie_id = r.movie_id
where r.user_id = #{user_id}
order by r.rating desc;
